import { useState } from "react";
import "./App.css";

function App() {
  const [newItem, setNewItem] = useState("");
  const [todos, setTodos] = useState([]);

  const handleSubmit = (e) => {
    e.preventDefault();
    let index = 0;
    setTodos((currentValue) => {
      return [...currentValue, { id: index, title: newItem, completed: false }];
    });
    index++;
  };
  return (
    <div className="app">
      <form onSubmit={handleSubmit} className="new-item">
        <div className="form-row">
          <label htmlFor="item">New Item</label>
          <input
            value={newItem}
            onChange={({ target }) => setNewItem(target.value)}
            type="text"
            id="item"
          />
          <button className="btn">Add</button>
        </div>
      </form>

      <h1>To Do List</h1>
      <ul className="list">
        {todos.map((todo) => {
          return (
            <li key={todo.id}>
              <label>
                <input type="checkbox" checked={todo.completed} />
                {todo.title}
              </label>
              <button className="btn btn-del">Delete</button>
            </li>
          );
        })}
      </ul>
    </div>
  );
}

export default App;
